/**
* Determines the level of logging that the function will output. This enables
* us to reduce the amount of "noise" generated by a battle-tested function,
* whilst enabling us to output additional logging on a new version.
*
* Setting `process.env.LOG_LEVEL` will overwrite the default value of `"info"`
* for this variable.
*
* @type {String}
*/
export const LOG_LEVEL = (process.env.LOG_LEVEL || 'info');

/**
 * The AWS Region that this Lambda function is currently executing in.
 *
 * _Note: This is normally populated by the Lambda execution environment._
 *
 * @type {String}
 */
export const AWS_REGION = process.env.AWS_REGION;

import Bunyan from 'bunyan';

import { LOG_LEVEL } from './globals';

/**
 * Custom serializer for errors that are thrown by the Error library
 * that this function utilizes.
 */
const ErrorSerializer = (err) => {
  const { message, name, type, origMessage, code = {} } = err;
  return { message, name, type, origMessage, detail: code };
};

/**
 * The options that the logger is initialised with.
 *
 * @see https://github.com/trentm/node-bunyan
 */
const options = {
  name: 'pipeline-transfer',
  level: LOG_LEVEL,
  serializers: {
    err: ErrorSerializer,
  },
};

/**
 * @external {Logger} https://github.com/trentm/node-bunyan
 */
const Logger = Bunyan.createLogger(options);

export default Logger;

import fs from 'fs';
import glob from 'glob';
import uuid from 'uuid/v4';
import Path from 'path';
import extract from 'extract-zip';
import { S3 } from 'aws-sdk';
import wrappedError from 'error/wrapped';
import typedError from 'error/typed';

import { AWS_REGION } from '../../../globals';

import File from './file';

/**
 * An error was encountered when attempting to download, commit and uncompress
 * the remote CodePipeline artifact.
 *
 * @type {Error}
 */
const readyError = wrappedError({
  message: 'Error occurred retrieving artifact',
  type: 'pipeline.artifact',
});

/**
 * The remote artifact could not be retrieved (S3 Error).
 *
 * @type {Error}
 */
const fetchError = wrappedError({
  message: 'Could not retrieve artifact.',
  type: 'pipeline.artifact.file',
});

/**
 * An error was encountered whilst attempting to read a file from inside the
 * artifact.
 *
 * @type {Error}
 */
const readError = wrappedError({
  message: 'Could not read artifact file "{filename}"',
  type: 'pipeline.artifact.file',
});

/**
 * The artifact was successfully downloaded, but an error occurred whilst
 * attempting to decompress it.
 *
 * @type {Error}
 */
const decompressionError = wrappedError({
  message: 'Failed to decompress artifact.',
  type: 'pipeline.artifact.file.decompression',
});

/**
 * The JSON key does not exist in the file.
 *
 * @type {Error}
 */
const keyNotFoundError = typedError({
  message: 'Key "{key}" not found in file "{filename}"',
  type: 'pipeline.artifact.file.json',
});

/**
 * This class handles retrieving CodePipeline artifact files as well as accessing
 * file contents in addition to attributes defined inside JSON files residing
 * within the artifact.
 *
 * As the artifacts passed to the function may reside within remote buckets which
 * this function does not have sufficient permissions to access, credentials are
 * used from the initial CodePipeline event object.
 */
export default class Artifact {
  /**
   * Constructs a new Artifact instance.
   *
   * @param {Object} s3Location - object representing a remote S3 location
   * @param {String} s3Location.bucketName - the name of the S3 bucket.
   * @param {String} s3Location.objectKey - the key of the file inside the S3 bucket.
   */
  constructor({ bucketName, objectKey }, { secretAccessKey, sessionToken, accessKeyId }) {
    /**
     * A local ID for this artifact.
     *
     * @type {String}
     */
    this.id = uuid();
    /**
     * A filename representing the name of the artifact once downloaded and stored
     * locally.
     *
     * @type {String}
     */
    this.filename = `${this.id}.zip`;
    /**
     * A fully resolved Path to the local artifact when downloaded and written
     * to the `/tmp/` directory.
     *
     * @type {String}
     */
    this.filepath = Path.join('/tmp/', this.filename);
    /**
     * A fully resolved path to the directory storing the decompressed contents
     * of the remote artifact in the `/tmp` directory.
     *
     * @type {String}
     */
    this.dir = Path.join('/tmp/', this.id);
    /**
     * The name of the S3 bucket which the remote artifact file resides in.
     *
     * @type {String}
     */
    this.bucketName = bucketName;
    /**
     * The key for the remote artifact file.
     *
     * @type {String}
     */
    this.objectKey = objectKey;
    /**
     * The AWS Secret Access Key which will be used to retrieve the remote artifact.
     *
     * @type {String}
     */
    this.secretAccessKey = secretAccessKey;
    /**
     * The AWS Session Token which will be used to retrieve the remote artifact.
     *
     * @type {String}
     */
    this.sessionToken = sessionToken;
    /**
     * The AWS Access Key ID which will be used to retrieve the remote artifact.
     *
     * @type {String}
     */
    this.accessKeyId = accessKeyId;
    /**
     * Determines if this artifact has been retrieved, decompressed and
     * written to local file storage.
     *
     * @type {Boolean}
     */
    this.isReady = false;
  }

  /**
   * Transform the properties received from CodePipeline into a new instance
   * of this class.
   *
   * This function maps the required properties from the CodePipeline invocation
   * event structure.
   *
   * @return {Artifact} a new artifact file with correctly mapped parameters.
   */
  static toArtifact(artifact, credentials = {}) {
    const { location: { s3Location } } = artifact;
    return new Artifact(s3Location, credentials);
  }

  /**
   * Returns an array which can be used to constuct a Map of artifacts.
   *
   * @return {Array[String, Artifact]}
   */
  static toArtifactMapEntry(artifact, credentials) {
    return [artifact.name, Artifact.toArtifact(artifact, credentials)];
  }

  /**
   * A utility function to ensure that this artifact has first retrived and
   * decompressed the remote artifact file, before any attempts are made
   * to retrieve artifact contents.
   *
   * @return {Boolean} true if ready, false otherwise.
   */
  async ready() {
    try {
      if (!this.isReady) {
        const data = await this.fetch();
        await this.write(data);
        await this.unzip();
        this.isReady = true;
      }
      return this.isReady;
    } catch (err) {
      throw readyError(err);
    }
  }

  /**
   * Returns an array of {@link File} instances which represent all matched
   * files from inside the retrieved, decompressed artifact.
   *
   * @param {String} select - a linux-style file glob to match files with.
   *
   * @return {Array[File]} an array of all matched {@link File} instances.
   */
  async match(select) {
    return new Promise((resolve, reject) => {
      try {
        const { dir: cwd } = this;
        glob(select, { cwd, nonull: false, nodir: true }, (err, files) => {
          if (err) {
            // TODO: throw error
          } else {
            resolve(files.map((file) => {
              const { dir, base: filename } = Path.parse(file);
              const path = Path.join(cwd, file);
              const data = fs.readFileSync(path, { encoding: 'binary' });
              return new File(dir, filename, data);
            }));
          }
        });
      } catch (err) {
        // TODO: catch error
        reject();
      }
    });
  }

  /**
   * Once {@link Artifact#ready} has resolved successfully, this function
   * can be used to retrieve a specific file's contents from within the
   * decompressed artifact.
   *
   * @return {Buffer} a utf-8 buffer of the file's contents.
   */
  get(filename) {
    try {
      const { dir } = this;
      const path = Path.join(dir, filename);
      return fs.readFileSync(path, { encoding: 'utf8' });
    } catch (err) {
      throw readError(err, { filename });
    }
  }

  /**
   * Once {@link Artifact#ready} has resolved successfully, this function
   * can be used to retrieve the value of a specific property key from within
   * a JSON file residing inside the decompressed artifact.
   *
   * @return [String] the value of the key from within a JSON file.
   */
  attribute(filename, key) {
    try {
      const obj = JSON.parse(this.get(filename));
      if (!Object.keys(obj).includes(key)) {
        throw keyNotFoundError({ filename, key });
      } else {
        return obj[key];
      }
    } catch (err) {
      throw readError(err, { filename });
    }
  }

  /**
   * @private
   *
   * Assuming that the remote artifact has been downloaded and written to
   * {@link Artifact#filepath}, attempt to extract the contents of the artifact
   * into {@link Artifact#dir}.
   *
   * @return {Boolean} true once contents have been successfully decompressed.
   */
  unzip() {
    return new Promise((resolve, reject) => {
      const { filepath, dir } = this;
      fs.mkdirSync(dir);
      extract(filepath, { dir }, (err) => {
        if (err) {
          reject(decompressionError(err));
        } else {
          resolve(true);
        }
      });
    });
  }

  /**
   * @private
   *
   * Write the contents of the compressed, remote artifact to {@link Artifact#filepath}.
   *
   * @return {Boolean} true once the file has been written.
   */
  async write(data) {
    const { filepath } = this;
    fs.writeFileSync(filepath, data, { encoding: 'utf8' });
    return true;
  }

  /**
   * @private
   *
   * Using the S3 client ({@link Artifact#client}) retrieve the artifact from
   * the remote S3 bucket.
   *
   * @return {Buffer} the buffer contents of the remote artifacts retrieved from S3.
   */
  async fetch() {
    try {
      const { bucketName, objectKey } = this;
      const params = { Bucket: bucketName, Key: objectKey };
      const { Body } = await this.client.getObject(params).promise();
      return Body;
    } catch (err) {
      throw fetchError(err);
    }
  }

  /**
   * Returns a new S3 client with credentials pre-configured.
   *
   * @type {S3}
   */
  get client() {
    const { secretAccessKey, sessionToken, accessKeyId } = this;
    return new S3({ region: AWS_REGION, accessKeyId, secretAccessKey, sessionToken });
  }
}

import Path from 'path';
import { S3 } from 'aws-sdk';
import wrappedError from 'error/wrapped';

import { AWS_REGION } from '../../../globals';

/**
 * The JSON key does not exist in the file.
 *
 * @type {Error}
 */
const uploadError = wrappedError({
  message: 'Failed to upload file with key "{key}"',
  type: 'pipeline.artifact.file.upload',
});

/**
 * Represents a file inside an artifact.
 *
 */
export default class File {
  /**
   * @param {String} path - the path to the file inside the artifact
   * @param {String} filename - The name of the file, including the extension name
   * @param {Buffer} data - A buffer of the files contents.
   */
  constructor(path, filename, data) {
    /**
     * The S3 key which represents this file (path + filename)
     *
     * @type {String}
     */
    this.key = Path.join('/', path, filename);
    /**
     * The data contained inside the file
     *
     * @type {Buffer}
     */
    this.data = data;
  }

  /**
   * Uploads the file to the specific remote S3 bucket, using the supplied
   * credentials and an optional prefix.
   *
   * @param {Object} opts - An options object
   * @param {String} opts.bucket - The bucket to upload the file to.
   * @param {Credentials} opts.credentials - An instantiated AWS.Credentials object.
   * @param {String} opts.prefix - An optional prefix to apply to this file.
   */
  async upload({ bucket: Bucket, credentials, prefix = '' }) {
    const { data, key } = this;
    try {
      const s3 = new S3({ region: AWS_REGION, credentials });
      const prefixedKey = Path.join('/', prefix, key);
      await s3.putObject({ Bucket, Key: prefixedKey, Body: data }).promise();
      return `s3://${Bucket}${prefixedKey}`;
    } catch (err) {
      throw uploadError(err, { key });
    }
  }
}

import typedError from 'error/typed';

/**
 * An unsupported parameter value object was encountered.
 *
 * @type {Error}
 */
const artifactParameterError = typedError({
  message: 'Unsupported object key "{keys}"',
  type: 'pipeline.attribute',
});

/**
 * The defined remote artifact does not exist or access has not been granted.
 *
 * @type {Error}
 */
const artifactNotFound = typedError({
  message: 'Artifact "{artifactName}" was referenced, but is not a valid InputArtifact.',
  type: 'pipeline.attribute.artifact.not_found',
});

/**
 * Indicates a remote artifact type
 *
 * @type {String}
 */
const REMOTE = 'REMOTE';

/**
 * Indicates a static artifact type
 *
 * @type {String}
 */
const STATIC = 'STATIC';

/**
 * Resolves an attribute that has been defined as part of the UserParameters.
 * An attribute can either be a static, hardcoded value or can be a reference
 * to a key, found inside a file present in an input artifact.
 */
export default class Attribute {
  /**
   * @param {String,Object} mapping - a mapping object, either a hardcoded value
   *  or a reference to a key, found inside a file present in an input artifact.
   * @param {Array[Artifact]} artifacts - an array of artifacts that any remote
   *  attribute value could be found inside.
   */
  constructor(mapping, artifacts) {
    /**
     * Either a hardcoded value (a string) or an object denoting a remote reference.
     *
     * @type {String,Object}
     */
    this.mapping = mapping;
    /**
     * An object containing {@link Artifact} instances, with the keys denoting
     * the logical name of the artifact inside CodePipeline.
     *
     * @type {Array[Artifact]}
     */
    this.artifacts = artifacts;
  }

  /**
   * Determine if the type of mapping that has been provided for the attribute
   * value is either a static value, or needs to be resolved using a remote
   * input artifact.
   *
   * @return {String} the type of attribute mapping provided
   */
  get type() {
    const { mapping } = this;
    if (typeof mapping !== 'string') {
      return REMOTE;
    }
    return STATIC;
  }

  /**
   * Resolves the value for the attribute, either returning the static attribute
   * value, or the resolved value from inside a remote artifact.
   *
   * @return {String} the attribute's value
   */
  async value() {
    const { type, mapping } = this;
    if (type === STATIC) {
      return mapping;
    }
    const properties = mapping['Fn::GetParam'];
    if (!properties) {
      const keys = Object.keys(mapping).join(', ');
      throw artifactParameterError({ keys });
    }
    return await this.fetch(...properties);
  }

  /**
   * @private
   *
   * Fetch the value of a specific parameter using the key found inside a json file
   * inside the specific artifact.
   *
   * @param {String} artifactName - the name of the artifact the file resides in.
   * @param {String} filename - The name of the JSON file inside the artifact
   * @param {String} key - The property key inside the JSON file
   *
   * @return {String} the value of the key inside the JSON file, inside the artifact.
   */
  async fetch(artifactName, filename, key) {
    const artifact = this.artifacts[artifactName];
    if (!artifact) {
      throw artifactNotFound({ artifactName });
    } else {
      await artifact.ready();
      return artifact.attribute(filename, key);
    }
  }
}

/**
 * Retrieves the name of the AWS Lambda function from the Lambda execution
 * environment variables.
 *
 * @type {String}
 */
export const FUNCTION_NAME = (process.env.AWS_LAMBDA_FUNCTION_NAME || 'default');

import { STS, Credentials } from 'aws-sdk';
import typedError from 'error/typed';
import wrappedError from 'error/wrapped';

import Attribute from './attribute';
import { FUNCTION_NAME } from './constants';

/**
 * The defined remote artifact does not exist or access has not been granted.
 *
 * @type {Error}
 */
const artifactNotFound = typedError({
  message: 'Artifact "{artifactName}" not a valid InputArtifact',
  type: 'pipeline.artifact.not_found',
});

/**
 * An error occurred whilst attempting to assume the role that was provided.
 *
 * @type {Error}
 */
const couldNotAssumeRole = wrappedError({
  message: 'Failed to assume role: "{roleArn}"',
  type: 'pipeline.destination.role',
});

/**
 * This class represents a single, defined destination which the user
 * has specified inside the UserParameters key.
 *
 * Responsible for first resolving any source input artifacts and matching
 * all files found inside the artifact. Then assuming the correct role to
 * access the destination, and uploading them, using the {@link File#upload}
 * method.
 */
export default class Destination {
  /**
   * @param {Object} destination - a valid src/destination object.
   * @param {String, Object} destination.roleArn - a static or remote reference
   *  to a IAM Role which has sufficient permissons for the destination S3 Bucket.
   * @param {String, Object} destination.bucket - a static or remote reference
   *  to a destination S3 bucket.
   * @param {Array[String]} destination.src - An array of source files to be
   *  found inside one or more input artifacts.
   * @param {String} destination.prefix - The prefix to apply to all uploaded
   *  source files in the destination S3 bucket.
   * @param {Object} artifacts - An object containing all
   *  {@link Artifact} instances with the property key being the artifacts name.
   */
  constructor({ roleArn, bucket, src, prefix = '/' }, artifacts) {
    /**
     * The AWS STS client used to assume roles inside the lambda function
     * for the appropriate S3 destination.
     *
     * @type {STS}
     */
    this.sts = new STS();
    /**
     * An array of {@link Artifact} (which may not be ready).
     *
     * @type {Array[Artifact]}
     */
    this.artifacts = artifacts;
    /**
     * A static or remote reference to a IAM Role which has sufficient
     * permissons for the destination S3 Bucket.
     *
     * @type {Attribute}
     */
    this.roleArn = new Attribute(roleArn, artifacts);
    /**
     * A static or remote reference to a destination S3 bucket.
     *
     * @type {Attribute}
     */
    this.bucket = new Attribute(bucket, artifacts);
    /**
     * An array of source files to be found inside one or more input artifacts.
     *
     * @type {Array[String]}
     */
    this.sources = src;
    /**
     * The prefix to apply to all uploaded source files in the
     * destination S3 bucket.
     *
     * @type {String}
     */
    this.prefix = prefix;
  }

  /**
   * Retrieves credentials by assuming the defined role, which (should) enable
   * IAM access to the remote S3 destination.
   *
   * @return {Object} an object containing AWS access keys.
   */
  async credentials() {
    const { sts } = this;
    const roleArn = await this.roleArn.value();
    try {
      const {
        Credentials: {
          AccessKeyId,
          SecretAccessKey,
          SessionToken,
        },
      } = await sts.assumeRole({
        RoleSessionName: FUNCTION_NAME,
        ExternalId: FUNCTION_NAME,
        RoleArn: roleArn,
      }).promise();
      return new Credentials(AccessKeyId, SecretAccessKey, SessionToken);
    } catch (err) {
      throw couldNotAssumeRole(err, { roleArn });
    }
  }

  /**
   * Retrieves all matched file objects from inside the "src" definition.
   * This function will resolve all matched file references, across all input
   * artifacts.
   *
   * @return {Array[File]} an array of file objects ready for upload.
   */
  async files() {
    const { artifacts, sources } = this;
    const files = sources.map(async (src) => {
      const [artifactName, glob] = src.split('::');
      const artifact = artifacts[artifactName];
      if (!artifact) {
        throw artifactNotFound({ artifactName });
      }
      await artifact.ready();
      return artifact.match(glob);
    });
    const sourceFiles = await Promise.all(files);
    return [].concat(...sourceFiles);
  }

  /**
   * Uploads all matched files from their source input artifacts to the
   * defined destinations, prefixed with the appropriate key.
   *
   * Note that this function first assumes the correct role for this destination.
   *
   * @return {Boolean} true if successful, throws otherwise.
   */
  async upload() {
    const { prefix } = this;
    const credentials = await this.credentials();
    const bucket = await this.bucket.value();
    const files = await this.files();
    const details = { bucket, credentials, prefix };
    return await Promise.all(files.map(f => f.upload(details)));
  }
}

import typedError from 'error/typed';

import Artifact from './artifact';
import Destination from './destination';
import validate from './validate';

/**
 * A validation error was encountered
 *
 * @type {Error}
 */
const inputArtifactsError = typedError({
  message: 'No InputArtifacts defined.',
  type: 'pipeline.start',
});

export default class Uploader {
  /**
   * Uploads artifacts to a remote S3 bucket defined inside an array of file
   * globs, of locations inside pipeline input artifacts.
   *
   * @example
   * ```
   *  {
   *     "CodePipeline.job": {
   *         "id": "11111111-abcd-1111-abcd-111111abcdef",
   *         "accountId": "111111111111",
   *         "data": {
   *             "actionConfiguration": {
   *                 "configuration": {
   *                     "FunctionName": "MyLambdaFunctionForAWSCodePipeline",
   *                     "UserParameters": "...."
   *                 }
   *             },
   *             "inputArtifacts": [
   *                 {
   *                     "location": {
   *                         "s3Location": {
   *                             "bucketName": "codepipeline-us-east-2-1234567890",
   *                             "objectKey": "rj90jda90ja90j09tj09jaf.zip"
   *                         },
   *                         "type": "S3"
   *                     },
   *                     "revision": null,
   *                     "name": "ArtifactName"
   *                 }
   *             ],
   *             "outputArtifacts": [],
   *             "artifactCredentials": {
   *                 "secretAccessKey": "....",
   *                 "sessionToken": "....",
   *                 "accessKeyId": "...."
   *             },
   *             "continuationToken": "A continuation token if continuing job"
   *         }
   *     }
   *  }
   * ```
   *
   * @param {Object} data - A CodePipeline data event object.
   */
  constructor({ data }) {
    /**
     * The data object as received from AWS CodePipeline.
     *
     * @type {Object}
     */
    this.data = data;
    /**
     * The parsed contents of the `UserParameters` string as defined inside the
     * action in the CodePipeline resource. This should be a valid array of objects,
     * similar to the example below:
     *
     * ```
     * [
     * {
     *   "roleArn": {
     *     "Fn::GetParam": ["DeployOutput", "Outputs.json", "AssetS3BucketTransferRole"]
     *   },
     *   "bucket": {
     *     "Fn::GetParam": ["DeployOutput", "Outputs.json", "AssetS3Bucket"]
     *   },
     *   "prefix": "s3/key/prefix/",
     *   "src": [
     *     "BuildOutput::*.js"
     *   ]
     * }
     * ]
     * ```
     *
     * @type {Object}
     */
    this.parameters = JSON.parse(this.data.actionConfiguration.configuration.UserParameters);
    const inputs = data.inputArtifacts;
    if (!inputs || inputs.length === 0) {
      throw inputArtifactsError({});
    }
    const mapper = a => Artifact.toArtifactMapEntry(a, data.artifactCredentials);
    /**
     * An array of {@link Artifact} objects which have been made available to
     * the CodePipeline action definition.
     *
     * @type {String}
     */
    this.artifacts = inputs.map(mapper).reduce((memo, [key, val]) => {
      const obj = memo;
      obj[key] = val;
      return obj;
    }, {});
  }

  /**
   * Retrieve validated (and defaulted) parameters which have been defined inside
   * the action in the CodePipeline resource.
   *
   * @return {Object} valid parameters to be used to construct the state machine input.
   */
  async userParameters() {
    return await validate(this.parameters);
  }

  /**
   * Maps all defined src/dest combinations (from {@link Uploader#userParameters}) to
   * destination objects ready for upload.
   *
   * @return {Array[Destination]} an array of Destination objects
   */
  async destinations() {
    const { artifacts } = this;
    const params = await this.userParameters();
    return params.map(p => new Destination(p, artifacts));
  }

  /**
   * Performs the intended operation and uploads source files from pipeine
   * artifacts to remote S3 locations by assuming the provided roles to
   * provide the correct permission sets.
   *
   * The promise resolves successfully after all source files have been uploaded.
   *
   * @return {Boolean} true if successful, throws otherwise.
   */
  async perform() {
    const destinations = await this.destinations();
    return await Promise.all(destinations.map(d => d.upload()));
  }
}

